INSERT INTO course (id, title, number, description, department) VALUES ('103','Flight Control Systems','MECH 480','Basic flight control and flight dynamics principles. Aircraft dynamic equations and performance data. Implementation of aircraft
control: control surfaces and their operations, development of thrust and its control; autopilot systems, their algorithms,
dynamics and interaction problems. Flight instruments, principles of operation and dynamics. Cockpit layouts - basic configuration,
ergonomic design, control field forces; advanced concepts in instruments, avionics and displays; HUD; flight management systems,
and communication equipment. Introduction to flight simulation: overview of visual, audio and motion simulator systems; advanced
concepts in flight simulators. Lectures: three hours per week. Laboratory: two hours per week, alternate weeks.','0');
INSERT INTO course (id, title, number, description, department) VALUES ('104','Avionic Navigation Systems','MECH 482','Basics of modern electronic navigation systems, history of air navigation, earth coordinate and mapping systems; basic theory
and analysis of modern electronic navigation instrumentation, communication and radar systems, approach aids, airborne systems,
transmitters and antenna coverage; noise and losses, target detection, digital processing, display systems and technology;
demonstration of avionic systems using flight simulator. Lectures: three hours per week.','0');
INSERT INTO course (id, title, number, description, department) VALUES ('105','','PHYS 204','','0');
INSERT INTO course (id, title, number, description, department) VALUES ('106','','PHYS 205','','0');
